    private void botonNuevoProcesoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botonNuevoProcesoActionPerformed
        habilitarCampos();
    }//GEN-LAST:event_botonNuevoProcesoActionPerformed

    private void botonAgregarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botonAgregarActionPerformed
        try {
            String nombre = campoNombre.getText();
            String tiempoArribo = campoTiempoArribo.getText();
            String duracion = campoDuracionProceso.getText();
            String memoriaRequerida = campoMemoriaRequerida.getText();
            controlador.registrarNuevoProceso(nombre, tiempoArribo, duracion, memoriaRequerida);
            deshabilitarCampos();
        } catch (NumberFormatException numberFormatException) {
            JOptionPane.showMessageDialog(null,
                    "Los datos ingresados no son num√©ricos.",
                    "Error Num√©rico",
                    JOptionPane.ERROR_MESSAGE);
        } catch (NullPointerException nullPointerException) {
            JOptionPane.showMessageDialog(null,
                    "Ingrese todos los datos correctamente.",
                    "Error",
                    JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_botonAgregarActionPerformed

    private void botonCancelarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botonCancelarActionPerformed
        deshabilitarCampos();
    }//GEN-LAST:event_botonCancelarActionPerformed

    private void botonEliminarProcesoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botonEliminarProcesoActionPerformed
        controlador.eliminarProceso(tablaProcesos.getSelectedRow());
    }//GEN-LAST:event_botonEliminarProcesoActionPerformed

    private void botonSiguienteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botonSiguienteActionPerformed
        controlador.iniciarVentanaSimulador();
    }//GEN-LAST:event_botonSiguienteActionPerformed

    private void habilitarCampos() {
        campoDuracionProceso.setText("");
        campoMemoriaRequerida.setText("");
        campoNombre.setText("");
        campoTiempoArribo.setText("");
        campoDuracionProceso.setEnabled(true);
        campoMemoriaRequerida.setEnabled(true);
        campoNombre.setEnabled(true);
        campoTiempoArribo.setEnabled(true);
        etiquetaDuracionTotal.setEnabled(true);
        etiquetaMemoriaRequerida.setEnabled(true);
        etiquetaNombre.setEnabled(true);
        etiquetaTiempoArribo.setEnabled(true);
        botonAgregar.setEnabled(true);
        botonCancelar.setEnabled(true);
    }

    private void deshabilitarCampos() {
        campoDuracionProceso.setText("");
        campoMemoriaRequerida.setText("");
        campoNombre.setText("");
        campoTiempoArribo.setText("");
        campoDuracionProceso.setEnabled(false);
        campoMemoriaRequerida.setEnabled(false);
        campoNombre.setEnabled(false);
        campoTiempoArribo.setEnabled(false);
        etiquetaDuracionTotal.setEnabled(false);
        etiquetaMemoriaRequerida.setEnabled(false);
        etiquetaNombre.setEnabled(false);
        etiquetaTiempoArribo.setEnabled(false);
        botonAgregar.setEnabled(false);
        botonCancelar.setEnabled(false);
    }

